import { useSSRSafeId } from "@react-aria/ssr"
import react, {useEffect, useContext, useState} from "react"
import { Table, Form, Button } from 'react-bootstrap'
import {TodoContext} from "../ToDoContext"
import { UpdateContext } from "../UpdateTodoContext"
import TodoRow from './TodosRow'
import { useHistory } from "react-router-dom"


const TodosTable= () => {
    const [allTodos, setAllTodos] = useContext(TodoContext)
    const [updateTodoInfo, setUpdateTodoInfo] = useContext(UpdateContext)

    let history = useHistory()

    const handleDelete = (id) => {
        fetch("http://localhost:5000/todos/" + id, {
            method: "DELETE",
            headers: {
                accept: 'application/json'
            }
        })
        .then(response => {
            return response.json()
        })
        .then(data => {
            const filteredTodos = allTodos.filter((todo) => todo.id !== id);
            setAllTodos(filteredTodos)
        })
    }

    const handleUpdate = (id) => {
        const todo = allTodos.filter(todo => todo.id === id)[0]
        setUpdateTodoInfo({
            TodoTitle: todo.title,
            TodoUnit: todo.unit,
            TodoId: id
        })
        history.push("/updateTodo")
    }

    const fetchTodos = async () => {
        const response = await fetch("http://localhost:5000")
        const todos = await response.json()
        setAllTodos(todos)
    }
    useEffect(() => {
        fetchTodos()
    }, [])

    return (
        <div>
            <Form onSubmit={filterTodoByTitle} inline>
                <FormControl value={searchByTitle} onChange={updateSearchByTitle} type="text" placeholder="Search by title" className="mr-sm-2" />
                <Button type="submit" variant="outline-primary">Search by title</Button>
            </Form>
            <Form onSubmit={filterTodoByUnit} inline>
                <FormControl value={searchByUnit} onChange={updateSearchByUnit} type="text" placeholder="Search by unit" className="mr-sm-2" />
                <Button type="submit" variant="outline-primary">Search by unit</Button>
            </Form>
            <div>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Unit</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {allTodos.map(todo => (
                            <TodoRow
                                id = {todo.id}
                                title = {todo.title}
                                unit = {todo.unit}
                                key = {todo.id}
                                handleDelete = {handleDelete}
                                handleUpdate = {handleUpdate}
                            />
                        ))}
                    </tbody>
                </Table>
            </div>
        </div>
    );
}

//<th>Id</th>
// 

export default TodosTable;
