import react, {useContext, useState} from "react"
import {Navbar, Nav, Form, FormControl, Button, Badge} from "react-bootstrap"
import {Link, useHistory } from "react-router-dom"
import { TodoContext } from "../ToDoContext"

const NavBar = () => {
    const [searchByTitle, setSearchByTitle] = useState("")
    const [searchByUnit, setSearchByUnit] = useState("")
    const [allTodos, setAllTodos] = useContext(TodoContext)

    let history = useHistory()

    const updateSearchByTitle = (e) => {
        setSearchByTitle(e.target.value)
    }

    const updateSearchByUnit = (e) => {
        setSearchByUnit(e.target.value)
    }

    const filterTodoByTitle = (e) => {
        e.preventDefault()
        const todo = allTodos.filter(todo => todo.title.toLowerCase().includes(searchByTitle.toLowerCase()) )
        setAllTodos(todo)
    }

    const filterTodoByUnit = (e) => {
        e.preventDefault()
        const todo = allTodos.filter(todo => todo.unit.toLowerCase() === searchByUnit.toLowerCase())
        setAllTodos(todo)
    }

    const sendHome = () => {
        history.push("/")
        window.location.reload(false)
    }

    return(
        <Navbar bg="dark" expand="lg" variant="dark">
            <Navbar.Brand href="#home">My ToDo List</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">            
                    <Badge className="mt-2" variant="primary">What's left to do : {allTodos.length}</Badge>
                </Nav>
                <Link to="/addtodo" className="btn btn-primary btn-sm mr-4">Add ToDo</Link>
                <Form onSubmit={filterTodoByTitle} inline>
                    <FormControl value={searchByTitle} onChange={updateSearchByTitle} type="text" placeholder="Search by title" className="mr-sm-2" />
                    <Button type="submit" variant="outline-primary">Search by title</Button>
                </Form>
                <Form onSubmit={filterTodoByUnit} inline>
                    <FormControl value={searchByUnit} onChange={updateSearchByUnit} type="text" placeholder="Search by unit" className="mr-sm-2" />
                    <Button type="submit" variant="outline-primary">Search by unit</Button>
                </Form>
                <Button color="red" onClick={sendHome} type="submit" variant="outline-primary">Home</Button>
            </Navbar.Collapse>
        </Navbar>
    );
}

export default NavBar;
