import react, {useContext, useState} from "react"
import {Navbar, Nav, Form, FormControl, Button, Badge} from "react-bootstrap"
import {Link, useHistory } from "react-router-dom"
import { TodoContext } from "../ToDoContext"

const NavBar = () => {
    const [searchByTitle, setSearchByTitle] = useState("")
    const [searchByUnit, setSearchByUnit] = useState("")
    const [allTodos, setAllTodos] = useContext(TodoContext)

    let history = useHistory()

    const sendHome = () => {
        history.push("/")
        window.location.reload(false)
    }

    return(
        <Navbar bg="dark" expand="lg" variant="dark">
            <Navbar.Brand href="#home">My ToDo List</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">            
                    <Badge className="mt-2" variant="primary">What's left to do : {allTodos.length}</Badge>
                </Nav>
                <Link to="/addtodo" className="btn btn-primary btn-sm mr-4">Add ToDo</Link>
                <Button onClick={sendHome} type="submit" variant="warning" size="lg">Home</Button>
            </Navbar.Collapse>
        </Navbar>
    );
}

export default NavBar;
